import UIKit

final class CurrenciesWireframe: BaseWireframe {

    // MARK: - Private properties -

    private let _storyboard = UIStoryboard(name: "Currencies", bundle: nil)

    // MARK: - Module setup -

    func configureModule(with viewController: CurrenciesViewController) {
        let interactor = CurrenciesInteractor()
        let presenter = CryptocurrencyPresenter(wireframe: self, view: viewController, interactor: interactor)
        viewController.presenter = presenter
    }

    // MARK: - Transitions -

    func show(with transition: Transition, animated: Bool = true) {
        let moduleViewController = _storyboard.instantiateViewController(ofType: CurrenciesViewController.self)
        configureModule(with: moduleViewController)

        show(moduleViewController, with: transition, animated: animated)
    }
    
    private func openCurrencyDetail(currency: Currency) {
        let wireframe = CurrencyDetailWireframe(navigationController: navigationController)
        wireframe.show(with: .push, currency: currency)
    }
}

// MARK: - Extensions -

extension CurrenciesWireframe: CurrenciesWireframeInterface {

    func navigate(to option: CurrenciesNavigationOption) {
        switch option {
        case .currencyDetail(let currency):
            openCurrencyDetail(currency: currency)
        }
    }
    
    func showLoadingView() {
        let alert = UIAlertController(title: nil, message: "Loading...", preferredStyle: .alert)
        alert.view.tintColor = UIColor.black
        let loadingIndicator = UIActivityIndicatorView(frame: CGRect(x: 10, y: 5, width: 50, height: 50))
        loadingIndicator.hidesWhenStopped = true
        loadingIndicator.activityIndicatorViewStyle = .gray
        loadingIndicator.startAnimating()
        alert.view.addSubview(loadingIndicator)
        self.navigationController.present(alert, animated: true, completion: nil)
    }
    
    func hideLoadingView() {
        self.dismiss(animated: true)
        //self.dismiss(animated: true, completion: nil)
    }
}
